import React from "react";

export default class Arrays extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            arr: [
              {  
                   name: "Soumava",
                   address: "Kolkata"
              },
              {  
                name: "Krishna",
                address: "Dwarka"
           }

           ]
        }
    }
    render(){
        return(
            <div>
                Hello from arrays Component
                {this.state.arr.map ( (single, index)=> {
                    return (
                        <div>
                            {single}
                        </div>
                    )
                })}
            </div>
        )
    }
}
