import React from "react";

export default class Adder extends React.Component{
    constructor(props){;
     super(props);

     this.state = {
         num1: 0,
         num2: 0,
         sum: 0
     }

    }

    handleNum1 = (e) => {
        this.setState({
            num1: e.target
        })
    }
    render(){
        return(
            <div>
                <input
                placeholder="enter a number"
                value={this.state.num1}
                onChange={this.handleNum1}
                />
                </div>
        )
    }
}
