import React from "react";

export default class Adder extends React.Component{
    constructor(props){;
     super(props);

     this.state = {
         num1: 0,
         num2: 0,
         sum: 0
     }

    }

    handleNum1 = (e) => {
        this.setState({
            num1: e.target.value
        })
    }

    handleNum2 = (e) => {
        this.setState({
            num2: e.target.value
        })
    }

    handleSum = () => {
        this.setState({
            sum: this.state.num1 + this.state.num2
        })
    }

    render(){
        return(
            <div>
                <input
                placeholder="enter a number"
                value={this.state.num1}
                onChange={this.handleNum1}
                />
                <br />
                <input
                placeholder="enter a number"
                value={this.state.num2}
                onChange={this.handleNum2}
                />
                <br />
                <button
                  onClick={this.handleSum}
                >
                    ADD
                </button>
                <br />
                sum is : {this.state.sum}
                </div>
        )
    }
}
