import React from "react";

export default class ArraysProject extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            task: [],
            taskInput: ""
        }
    }

    handleInput = (e) => {
        this.setState({
            taskInput: e.target.value
        })
    }

    render() {
        return (
            <div>
                <input
                    value={this.state.taskInput}
                    onChange={this.handleInput}
                />
                <button>
                    Add task
